import React from 'react'
import './index.css'
import { Typography, Button } from '@mui/material'

const tasks = [
    {
        no: 1,
        dateTime: "2025-03-17 10:30 AM",
        task: "Fix Bugs",
        description: "Fix login page validation issues",
        action: ':',
    },
    {
        no: 2,
        dateTime: "2025-03-18 03:00 PM",
        task: "Meeting with Client",
        description: "Discuss project requirements",
        action: ':',
    },
    {
        no: 3,
        dateTime: "2025-03-19 11:45 AM",
        task: "Code Review",
        description: "Review PR for Task Manager API",
        action: ':',
    }
];

console.log(tasks);


const TaskManager = () => {
    return (
        <div>
            <Typography variant='h4' className='task-heading'>Tasks Management</Typography>
            <Button variant="contained" className='add-button'>Add task</Button>
            <div className='heading-table'>
                <Typography variant='p'>No</Typography>
                <Typography variant='p'>Date & Time</Typography>
                <Typography variant='p'>Task</Typography>
                <Typography variant='p'>Description</Typography>
                <Typography variant='p'>Action</Typography>
            </div>
            <ul>
                {tasks.map((each) => {
                    return <li key={each.no}></li>
                })}
            </ul>
        </div>
    )
}

export default TaskManager
