import React, { useState } from 'react'
import { SlOptionsVertical } from "react-icons/sl";
import './index.css'
import { Typography, Button } from '@mui/material'

const tasks = [
    {
        no: 1,
        dateTime: "2025-03-17 10:30 AM",
        task: "Fix Bugs",
        description: "Fix login page validation issues",
        action: ':',
    },
    {
        no: 2,
        dateTime: "2025-03-18 03:00 PM",
        task: "Meeting with Client",
        description: "Discuss project requirements",
        action: ':',
    },
    {
        no: 3,
        dateTime: "2025-03-19 11:45 AM",
        task: "Code Review",
        description: "Review PR for Task Manager API",
        action: ':',
    }
];


const TaskManager = () => {
    const [selectedTask, setSelectedTask] = useState(null)
    const [menuVisible, setMenuVisible] = useState(false)
    const [menuPosition, setMenuPosition] = useState({ top: 0, left: 0 });

    const handleShowMenu = (event, task) => {
        setSelectedTask(task);
        setMenuVisible(true);
        setMenuPosition({ top: event.clientY, left: event.clientX });
    };

    const handleClose = () => {
        setMenuVisible(false);
        setSelectedTask(null);
    };

    return (
        <div>
            <Typography variant='h4' className='task-heading'>Tasks Management</Typography>
            <button className='add-button'>Add task</button>
            <div className='heading-table' style={{ marginLeft: '40px' }}>
                <Typography variant='p' sx={{ width: '4%' }}>No</Typography>
                <Typography variant='p' sx={{ width: '26%' }}>Date & Time</Typography>
                <Typography variant='p' sx={{ width: '13%' }}>Task</Typography>
                <Typography variant='p' sx={{ width: '45%' }}>Description</Typography>
                <Typography variant='p' sx={{ width: '10%' }}>Action</Typography>
            </div>
            <ul style={{ width: '95%' }}>
                {tasks.map((each) => {
                    return <li key={each.no} className='heading-table list'>
                        <Typography variant='p' sx={{ width: '2%', textAlign: 'start' }}>{each.no}</Typography>
                        <Typography variant='p' sx={{ width: '25%' }}>{each.dateTime}</Typography>
                        <Typography variant='p' sx={{ width: '15%' }}>{each.task}</Typography>
                        <Typography variant='p' sx={{ width: '45%' }}>{each.description}</Typography>
                        <Button sx={{ width: '5%', outline: 'none' }} onClick={(event) => handleShowMenu(event, each)}>
                            <SlOptionsVertical />
                        </Button>
                    </li>
                })}
            </ul>
            {menuVisible && (
                <div
                    className="popup-menu"
                    style={{ top: menuPosition.top, left: menuPosition.left }}
                >
                    <button onClick={() => { }}>
                        Edit
                    </button>
                    <button onClick={() => { }}>
                        Delete
                    </button>
                </div>
            )}
            {menuVisible && <div className="backdrop" onClick={handleClose}></div>}
        </div>
    )
}

export default TaskManager
